mapper.update:
		
			UPDATE blog
		

		
			
				
   			 		SET
						logofile = #{logofile}
				
			

			
				
					SET
						blogtitle = #{blogtitle}
				
			

			
				
					SET
						logofile = #{logofile},
						blogtitle = #{blogtitle}
				
			
		

		
		WHERE
			id = #{id}
